(()=>{document.addEventListener("DOMContentLoaded",function(){const e=document.querySelector("header");window.addEventListener("scroll",function(){window.scrollY>50?e.classList.add("scrolled"):e.classList.remove("scrolled")});const t=document.querySelector(".nav-toggle"),n=document.querySelector("nav");t.addEventListener("click",function(){e.classList.toggle("nav-open")}),n.addEventListener("click",function(t){"A"===t.target.tagName&&e.classList.remove("nav-open")})});const e=["#3BBFAD","#2E9AE6","#F7C71E","#EE6DAF","#66D4C5"];document.querySelectorAll("h2").forEach(t=>{const n=null!==t.closest("#kontakt")?e.filter(e=>"#F7C71E"!==e):e;t.innerHTML=t.textContent.split("").map((e,t)=>`<span style="color:${n[t%n.length]}">${e}</span>`).join("")})})();